                    <div className="card">
                    <p>O conteúdo é : {item.conteudo}</p>
                    <p>Status: {item.status}</p> 
                    <p>Bloco: {item.bloco}</p>
                    </div>
                    )
                }
          </div>
      )
  }

class Content extends React.Component {
    render(){
        return (
            func(conteudos)
        )
    }
}

export default Content
