import React from 'react';
import './App.css';
import data from './data;'

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      topics: data
    }
  }

  addNewTopic(topic){
    this.setState((prevState) => ({topics: [...prevState.topics, topic]}))
  }



}



export default App;
