import React, { Component } from 'react'
import './Form.css';

export default class Form extends Component {
    constructor() {
        super()

        this.state = {
            nome: '',
            idade: 0,
            casado: false,
            cidade:'',
            estado: '',
        }
    }

    render() {
        return (
            <div className='form'>
                <label>Nome
                    <input type='text' name='nome' />
                </label>
                <label>Idade:
                    <input type='number' name='idade' />
                </label>
            </div>
        )
    }
}
