import React from 'react';
import Nome from './Nome';
import '../Css/Form.css'
import '../Css/Button.css'
import Endereco from './Endereco';
import Cidade from './Cidade';
import Estado from './Estado';
import Buttons from './Buttons';
import Email from './Email';
import Cpf from './Cpf';
import Moradia from './Moradia';
import Emprego from './Emprego';

export default class Form extends React.Component{
    constructor() {
        super()

        this.state = {
            nome: '',
            email: '',
            cpf: '',
            endereco: '',
            cidade: '',
            estado: '',
            tipo: '',
            resumo: '',
            cargo: '',
            cargoDesc:'',
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleClean = this.handleClean.bind(this)
    }

    handleChange({target}) {
        const { name } = target
        const value = target.type === 'radio' ? target.id : target.value
        if (name === 'nome'){
            this.setState({
                [name]: value.toUpperCase()
            })
        } else {
        this.setState({
            [name]: value
        })
        }
    }

    handleClean() {
        this.setState({
            nome: '',
            email: '',
            cpf: '',
            endereco: '',
            cidade: '',
            estado: '',
            tipo: '',
            resumo: '',
            cargo: '',
            cargoDesc:'',
        })
    }


    render() {
        // const { handleChange } = this
        return (
            <ul>
            <fieldset className='form'>
                <legend>Curricúlo 2.0</legend>
                <Nome value={this.state.nome} handleChange={this.handleChange}/>
                <Email value={this.state.email} handleChange={this.handleChange}/>
               <Cpf value={this.state.cpf} handleChange={this.handleChange}/>
                <Endereco value={this.state.endereco} handleChange={this.handleChange}/>
                <Cidade value={this.state.cidade} handleChange={this.handleChange}/>
                <Estado value={this.state.estado} handleChange={this.handleChange}/>
                <Moradia value={this.state.moradia} handleChange={this.handleChange}/>
            </fieldset>
                <Emprego value={this.state.cargo} handleChange={this.handleChange}/>
            <fieldset className='form'>
                <legend>Escolaridade, Cursos e informações adicionais</legend>
                <li>
                    <textarea name='resumo' maxLength='1000' required onChange={this.handleChange} placeholder='Resumo do Cúrriculo' className='textArea2' />
                </li>
            </fieldset>
            <div className='divBtn'>
                <Buttons text={'Enviar'} classe={'btn first'}/>
                <Buttons text={'Limpar'} classe={'btn2 first2'} handleClean={this.handleClean}/>
            </div>
            </ul>
        )
    }
}
