import React from 'react'

export default class Nome extends React.Component {
    render() {
        const { value, handleChange } = this.props
        return (
            <label>Nome
                <input 
                value={value} type='text' name='nome' maxLength='40' required onChange={handleChange}/>
            </label>
        );
    }
}
