import React from 'react';
import Nome from './Nome';
import '../Css/Form.css'
import '..'
import Endereco from './Endereco';
import Cidade from './Cidade';
import Estado from './Estado';

export default class Form extends React.Component{
    constructor() {
        super()

        this.state = {
            nome: '',
            email: '',
            cpf: '',
            endereco: '',
            cidade: '',
            estado: '',
            tipo: '',
            resumo: '',
            cargo: '',
            cargoDesc:'',
        }
        this.handleChange = this.handleChange.bind(this)

    }

    handleChange({target}) {
        const { name } = target
        const value = target.type === 'radio' ? target.id : target.value
        if (name === 'nome'){
            this.setState({
                [name]: value.toUpperCase()
            })
        } else {
        this.setState({
            [name]: value
        })
        }
    }


    render() {
        // const { handleChange } = this
        return (
            <ul>
            <fieldset className='form'>
                <legend>Curricúlo 2.0</legend>
                <Nome value={this.state.nome} handleChange={this.handleChange}/>
                <li>
                    <label>Email</label>
                    <input type='text' required maxLength='50' onChange={this.handleChange} name='email'/>
                </li>
                <li>
                    <label>CPF</label>
                    <input type='text' required maxLength='11' onChange={this.handleChange} name='cpf'/>
                </li>
                <Endereco value={this.state.endereco} handleChange={this.handleChange}/>
                <Cidade value={this.state.cidade} handleChange={this.handleChange}/>
                <Estado value={this.state.estado} handleChange={this.handleChange}/>
                <p>Moradia:</p>
                <div className='radio'>
                    <input type='radio' id='casa' name='tipo' onChange={this.handleChange} required></input>
                    <label>Casa</label>
                    <input type='radio' id='apartamento' name='tipo' onChange={this.handleChange} required></input>
                    <label>Apartamento</label>
                </div>
            </fieldset>
            <fieldset className='form'>
                <legend>Dados do último emprego</legend>
                <li>
                    <label>Cargo</label>
                    <input type='text' required maxLength='401' onChange={this.handleChange} name='cargo'/>
                </li>
                <li>
                    <textarea name='cargoDesc' maxLength='500' required onChange={this.handleChange} placeholder='Resumo do Cargo' className='textArea' />
                </li>
            </fieldset>
            <fieldset className='form'>
                <legend>Escolaridade, Cursos e informações adicionais</legend>
                <li>
                    <textarea name='resumo' maxLength='1000' required onChange={this.handleChange} placeholder='Resumo do Cúrriculo' className='textArea2' />
                </li>
            </fieldset>
            <button>Enviar</button>
            </ul>

        )
    }
}
