import React from 'react';
import Nome from './Nome';
import '../Css/Form.css'
import Endereco from './Endereco';

export default class Form extends React.Component{
    constructor() {
        super()

        this.state = {
            nome: '',
            email: '',
            cpf: '',
            endereco: '',
            cidade: '',
            estado: '',
            tipo: '',
        }
        this.handleChange = this.handleChange.bind(this)

    }

    handleChange({target}) {
        const { name } = target
        const value = target.type === 'checkbox' ? target.checked : target.value
        if ([name] === 'nome'){
            this.setState({
                [name]: value
            })
        }
        this.setState({
            [name]: value
        })
    }


    render() {
        // const { handleChange } = this
        return (
            <fieldset className='form'>
                <legend>Curric√∫lo 2.0</legend>
                <Nome value={this.state.nome} handleChange={this.handleChange}/>
                <label>Email
                    <input type='text' required maxLength='50' onChange={this.handleChange} name='email'/>
                </label>
                <label>CPF
                    <input type='text' required maxLength='11' onChange={this.handleChange} name='cpf'/>
                </label>
                <Endereco value={this.state.endereco} handleChange={this.handleChange}/>


            </fieldset>
        )
    }
}
