  render() {
    const { showTimer } = this.state
    return (
      <div>
        <h1>Hora da meditação</h1>
        {showTimer ? <Timer /> : null}
        <button
        type="button"
        onClick={ () => {}}
        >
          Esconder Timer || Mostrar Timer
        </button>
      </div>

    )
  }
}
